import React from 'react';
import Todo from './Todo';
import { useState, useEffect } from 'react';

const todos = [
    {"description": "Einkaufen", done: true},
    {"description": "Sport", done: false},
    {"description": "Programmieren", done: false},
];

const Todolist = () => {
  const [opencount, countOpenTodos] = useState(0);
  const [todos, setTodos] = useState [alltodos= true];

  const countOpen = () => {
    const donetodos = todos.filter((item) => {
      return !item.done
    });
    countOpenTodos(donetodos.length);
  };

  const changeTodo = (index) => {
    const newTodos = [...todos];
    if(newTodos[index].done){
      newTodos[index].done = false
    } else {
      newTodos[index].done = true;
    } 
    setTodos(newTodos);
  }

  useEffect(() => {
    countOpen();
  }, [opencount]);

  return (
    <div className= "shadow-10xl">
      <div className= "container text-center bg-slate-900 text-white py-4 font-mono">
        <h1 className= "text-4xl">Unsere Todos</h1>
        <h2>Offene Todos: {opencount}</h2>
      </div>

      {todos.map((item, index) => {
        return (
          <Todo
            description= {item.description}
            done= {item.done}
            key= {index}
            index= {index} 
            onChangeTodo= {changeTodo}
          ></Todo>
        );
      })}
    </div>
  );
};

export default Todolist;
