import Todo from './Todo';
import { useState, useEffect } from 'react';


const Todolist = () => {
  const [opencount, countOpenTodos] = useState(0);
  const [todos, setTodos] = useState([]);
  const [textinput, settextinput] = useState("");

  const changeText = (e) => {
    settextinput(e.target.value);
  };

  const submit = (e) => {
    e.preventDefault();
    const newTodos = [...todos, { description: textinput, done: false }];
    setTodos(newTodos);
  };

  const countOpen = () => {
    const donetodos = todos.filter((item) => {
      return !item.done;
    });
    countOpenTodos(donetodos.length);
  };

  const changeTodo = (index) => {
    const newTodos = [...todos];
    if (newTodos[index].done) {
      newTodos[index].done = false;
    } else {
      newTodos[index].done = true;
    } 
    setTodos(newTodos);
  };

  const deleteTodo = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  useEffect(() => {
    countOpen();
    localStorage.setItem("items", JSON.stringify(todos));
  }, [todos]);

  return (
    <div className= "shadow-10xl">
      <div className= "text-center bg-slate-900 text-white py-4 font-mono">
        <h1 className= "text-4xl">Unsere Todos</h1>
        <h2>Offene Todos: {opencount}</h2>
        <form className='grid grid-cols-2 py-3'>
          <input
           onChange={changeText}
           type="text"
           placeholder="Neues Todo..."
           className='col-span-2 py-1 text-black'
          ></input>
          <input
           onClick={submit}
           type="submit" 
           value="Add Todo" 
           className='col-span cursor-pointer bg-slate-500 text-white-200'
          ></input>
        </form>
      </div>

      {todos.map((item, index) => {
        return (
          <Todo
            description= {item.description}
            done= {item.done}
            key= {index}
            index= {index} 
            onChangeTodo= {changeTodo}
            onDeleteTodo= {deleteTodo}
          ></Todo>
        );
      })}
    </div>
  );
};

export default Todolist;
