    <div>
     <div
        className={
          done 
            ? "flex justify-between items-center p-2 bg-green-600 text-white"
            : "flex justify-between items-center p-2 bg-red-500 text-white"
       }
      >
        <h1 className="text-lg cursor-pointer" onClick={changeTodo}>
          {description}
        </h1>
